package com.example.search_pic.presentation

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.View
import com.example.search_pic.R
import com.example.search_pic.utils.Network.loadImageFromNetworkWithGlide
import kotlinx.android.synthetic.main.fragment_main.*
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers.IO
import kotlinx.coroutines.Dispatchers.Main
import kotlinx.coroutines.async
import kotlinx.coroutines.launch
import kotlinx.coroutines.withContext

class MainFragment : Fragment(R.layout.fragment_main) {



    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        val programming = listOf(" https://github.githubassets.com/images/modules/site/social-cards/package-registry.png",
            "data:image/png;base64,",
            "https://infostart.ru/upload/iblock/206/206715a232219a2ad06670a2b2db4bfb.jpg",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTW1EvOngbZpriXvBe0eZ3ShOgLszlbch_xiw&usqp=CAU")
        val animals = listOf("https://a-z-animals.com/media/2021/01/mammals-400x300.jpg",
            "https://upload.wikimedia.org/wikipedia/commons/1/14/Animal_diversity.png",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"
        )
        val cars = listOf("https://m.media-amazon.com/images/I/71+mhWHnBdL._SY445_.jpg",
            "https://cdn.europosters.eu/image/1300/posters/cars-compilation-i10411.jpg",
            "https://m.media-amazon.com/images/M/MV5BNzFiNWE2ZTktYzBhZS00ZmMwLTg5NDYtYTkwM2I0NjZhMTExXkEyXkFqcGdeQXVyNzU1NzE3NTg@._V1_QL75_UX500_CR0,47,500,281_.jpg")


        CoroutineScope(IO).launch {
            testCoroutines()
        }
        btnFindPicture.setOnClickListener {
            CoroutineScope(IO).launch {
                var currentList: List<String>? = null
                when (etFindPicture.text.toString()) {
                    "programming" -> currentList = programming
                    "animals" -> currentList = animals
                    "cars" -> currentList = cars
                }
                withContext(Main) {
                    currentList?.random()?.let { randomPicture ->
                        loadImageFromNetworkWithGlide(
                            context = requireContext(),
                            imageURL = randomPicture,
                            imageView = ivPicture
                        )
                    }
                }

            }
        }
    }
    suspend fun returnSomeNomber(number: Int):Int {
        return number+1
    }
    suspend fun testCoroutines(): Int {
        val coroutine = CoroutineScope(IO).async {
            returnSomeNomber(number = 3)
        }
        val number = coroutine.await()

        return number
    }

}